<!DOCTYPE html><html><head>	<title>Circunscripciones</title>	<meta charset="utf-8" />	<meta name="viewport" content="width=device-width, initial-scale=1.0">	<link rel="stylesheet" href="http://cdn.leafletjs.com/leaflet-0.7.3/leaflet.css" />	<script src="http://cdn.leafletjs.com/leaflet-0.7.3/leaflet.js"></script>	<script src="js/estados.geojson.js" type="text/javascript"></script>	<style> #map {  border-top: 2px solid #48265C; border-bottom: 2px solid #48265C;} </style></head><body>	<div id="map" style="width: 235px; height:323px;"></div>	<script>		var map = L.map('map').setView([22.674847351188536, -101.77734374999999], 5);				//primera		var primera = [2, 3, 8, 10, 14, 18, 25, 26];		//segunda		var segunda = [1, 5, 11, 19, 22, 24, 28, 32];		//tercera		var tercera = [4, 7, 20, 23, 27, 30, 31];		//cuarta		var cuarta = [9, 12, 17, 21, 29];		//quinta		var quinta = [6, 13, 15, 16];				var polygonsGroup = new L.LayerGroup();				L.tileLayer('https://{s}.tiles.mapbox.com/v3/{id}/{z}/{x}/{y}.png', {			maxZoom: 12,			attribution: 'Map data &copy; <a href="http://openstreetmap.org">OpenStreetMap</a> contributors, ' +				'<a href="http://creativecommons.org/licenses/by-sa/2.0/">CC-BY-SA</a>, ' +				'Imagery Â© <a href="http://mapbox.com">Mapbox</a>',			id: 'examples.map-20v6611k'		}).addTo(map);		function onEachFeature(feature, layer) {			layer.setStyle({				fillOpacity: 0.9, opacity: 0.8, weight: 2, color: "#432B4D", fillColor: "#3E2F52"			});		}				var geojson = L.geoJson(GeoJson, {			filter: function (feature, layer) {				var indexof = tercera.indexOf(parseInt(feature.properties.CVE_ENT));								if(indexof != -1) {					return feature.properties.CVE_ENT;				}								return false;			},			onEachFeature: onEachFeature		}).addTo(map);				map.fitBounds(geojson);	</script></body></html>