<?php $state = "17"; ?><!DOCTYPE html><html><head>	<title>Leaflet GeoJSON Example</title>	<meta charset="utf-8" />	<meta name="viewport" content="width=device-width, initial-scale=1.0">	<link rel="stylesheet" href="http://cdn.leafletjs.com/leaflet-0.7.3/leaflet.css" />	<script src="http://cdn.leafletjs.com/leaflet-0.7.3/leaflet.js"></script>	<script src="js/estado-<?php echo $state;?>.geojson.js" type="text/javascript"></script></head><body>	<div id="map" style="width: 600px; height: 400px"></div>	<script>		var map = L.map('map').setView([22.674847351188536, -101.77734374999999], 5);		L.tileLayer('https://{s}.tiles.mapbox.com/v3/{id}/{z}/{x}/{y}.png', {			maxZoom: 18,			attribution: 'Map data &copy; <a href="http://openstreetmap.org">OpenStreetMap</a> contributors, ' +				'<a href="http://creativecommons.org/licenses/by-sa/2.0/">CC-BY-SA</a>, ' +				'Imagery Â© <a href="http://mapbox.com">Mapbox</a>',			id: 'examples.map-20v6611k'		}).addTo(map);		function onEachFeature(feature, layer) {			var popupContent = "<p>I started out as a GeoJSON " +					feature.geometry.type + ", but now I'm a Leaflet vector!</p>";			if (feature.properties && feature.properties.popupContent) {				popupContent += feature.properties.popupContent;			}			layer.bindPopup(popupContent);			map.fitBounds(layer);			map.fitBounds(layer);		}				L.geoJson(distritosGeoJson, {			onEachFeature: onEachFeature		}).addTo(map);	</script></body></html>